mapper.select:
select * from lookJob
where jobno = #{jobno}
order by jobno desc

select tno, title, writers, to_char(sysdate, 'YYYY-MM-DD')
test_date, to_char(sysdate, 'HH:MI') test_time from test
where tno = #{tno}

select * from images
where tno = #{tno}
order by tno desc

select * from find
where fno = #{fno}
order by fno desc

select * from f_images
where fno = #{fno}
order by fno desc

select * from find
where company = #{company}
or fday = #{fday}
or course_name = #{course_name}

select * from tbl_reference
where rno = #{rno}
order by rno desc

select r_images r_image, rno from r_file
where rno = #{rno}
order by rno desc

select next,prev 
	from (
	    select rno, 
	    lead(rno,1) over(order by rno) as next, 
	    lag(rno,1) over(order by rno) as prev from tbl_reference
	)
where rno = #{rno}
